import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { WgerService } from '../wger.service';

@Component({
  selector: 'app-semana',
  templateUrl: './semana.component.html',
  styleUrls: ['./semana.component.css']
})
export class SemanaComponent implements OnInit {
  diasSemana = ['Lunes', 'Martes', 'Miércoles', 'Jueves', 'Viernes', 'Sábado', 'Domingo'];
  ejerciciosSemana: any[] = [];
  error: string = '';

  constructor(private wgerService: WgerService, private router: Router) {}

  ngOnInit(): void {
    // Obtener rutina desde la API de wger
    this.wgerService.getRutina().subscribe(
      (rutina: any) => {
        // Iterar sobre los días de la semana
        for (let dia of this.diasSemana) {
          // Buscar los ejercicios para el día actual en la rutina
          const ejerciciosDia = rutina.results.filter((ejercicio: any) => {
            return ejercicio.day === dia.toLowerCase();
          });
          // Agregar los ejercicios encontrados para el día actual a la lista de ejercicios de la semana
          this.ejerciciosSemana.push({ dia: dia, ejercicios: ejerciciosDia });
        }
      },
      (error) => {
        this.error = 'Error al obtener la rutina. Por favor, inténtalo de nuevo más tarde.';
      }
    );
  }

  verDetallesEjercicio(id: number): void {
    this.router.navigate(['/ejercicio', id]);
  }
}
