import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { WgerService } from '../wger.service';
import { Observable, take, forkJoin} from 'rxjs';
import { map } from 'rxjs/operators';



@Component({
  selector: 'app-ejercicios',
  templateUrl: './ejercicios.component.html',
  styleUrls: ['./ejercicios.component.scss']
})
export class EjerciciosComponent implements OnInit {

  ejercicios: any[] = [];
  imagenesEjercicios: { [id: number]: any[] } = {};

  constructor(private http: HttpClient, private wgerservice: WgerService) { }

  ngOnInit() {
    this.wgerservice.getExercises().pipe(
      take(5),
      forkJoin(
        this.ejercicios.map(ejercicio => this.wgerservice.getImagesEjercicios(ejercicio.id))
      )
    ).subscribe(([ejercicios, imagenes]) => {
      this.ejercicios = ejercicios;
      imagenes.forEach((imagenesEjercicio, i) => {
        this.imagenesEjercicios[ejercicios[i].id] = imagenesEjercicio.results;
      });
    });
  }




}

<div *ngIf="ejercicios">
  <div *ngFor="let ejercicio of ejercicios">
    <h2>{{ ejercicio.name }}</h2>
    <p>{{ ejercicio.description }}</p>

    <div *ngIf="imagenesEjercicios[ejercicio.id]">
      <img *ngFor="let imagen of imagenesEjercicios[ejercicio.id]" [src]="imagen.image" alt="{{ imagen.caption }}">
    </div>
  </div>
</div>
