import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { WgerService } from '../wger.service';

@Component({
  selector: 'app-ejercicios',
  templateUrl: './ejercicios.component.html',
  styleUrls: ['./ejercicios.component.css']
})
export class EjerciciosComponent implements OnInit {

  ejercicios: any[] = [];
  imagenesEjercicios: { [id: number]: any[] } = {};

  constructor(private http: HttpClient, private wgerservice: WgerService) { }

  ngOnInit() {
    this.wgerservice.getExercises().subscribe(data => {
      this.ejercicios = data.results;
      this.ejercicios.forEach(ejercicio => {
        this.wgerservice.getImagesEjercicios(ejercicio.id).subscribe(imagenes => {
          this.imagenesEjercicios[ejercicio.id] = imagenes.results;
        });
      });
    });
  }




}

