import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { WgerService } from '../wger.service';
import { Observable, take, forkJoin} from 'rxjs';
import { map } from 'rxjs/operators';



@Component({
  selector: 'app-ejercicios',
  templateUrl: './ejercicios.component.html',
  styleUrls: ['./ejercicios.component.scss']
})
export class EjerciciosComponent implements OnInit {

  ejercicios: any[] = [];
  imagenesEjercicios: {[ejercicioId: number]: any[]} = {};

  constructor(private wgerservice: WgerService) { }

  ngOnInit() {
    this.wgerservice.getExercises().subscribe(data => {
      this.ejercicios = data.results;
      this.getImages();
    });
  }

  getImages(){
    const firstFiveExercises = this.ejercicios.slice(0, 5);
    const observables = firstFiveExercises.map(ejercicio => this.wgerservice.getImagesEjercicios(ejercicio.id));
    forkJoin(observables).subscribe(results => {
      results.forEach((imagenes, index) => {
        const ejercicio = firstFiveExercises[index];
        this.imagenesEjercicios[ejercicio.id] = imagenes.results;
      });
    });

  }
}

<div *ngIf="ejercicios">
  <div *ngFor="let ejercicio of ejercicios">
    <h2>{{ ejercicio.name }}</h2>
    <p>{{ ejercicio.description }}</p>

    <div *ngIf="imagenesEjercicios[ejercicio.id]">
      <img *ngFor="let imagen of imagenesEjercicios[ejercicio.id]" [src]="imagen.image" alt="{{ imagen.caption }}">
    </div>
  </div>
</div>
