import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-menu-principal',
  templateUrl: './menu-principal.component.html',
  styleUrls: ['./menu-principal.component.scss']
})
export class MenuPrincipalComponent implements OnInit {

  title = 'GymBros';

  constructor() { }

  ngOnInit(): void {
  }

}
<div class='menu-principal' style="text-align: center;">
  <a routerLink="/inicio">Inicio</a>
  <br>
  <a routerLink="/rutina">Rutina</a>
  <br>
  <a routerLink="/calendario">Calendario</a>

  <br>
  <a routerLink="/ejercicios">Lista de Ejercicios</a>
  <br>
  <br><span><b>{{title}}</b></span>
  <br><br><a routerLink="/usuario">Usuario</a>
</div>
